def cli():
    print('envkp controls GitHub staled environments')
    print('Usage:')
    print('     envkp seek --repo=GITHUB_REPONAME [ --verbose ]')
    print('     envkp clean --repo=GITHUB_REPONAME [ --force ]')
    print()


def dump():
    print('envkp-dump')
    print('Usage:')
    print('     envkp-dump --repo=GITHUB_REPONAME [ --verbose ]')
    print()
